/**
 * The showStars modifier shows stars in dependent of the score
 *
 * @param string $urlType      The url type (admin, user, etc.)
 * @param string $urlFunc      The url func (view, display, edit, etc.)
 * @param array  $urlArguments The argument array containing ids and other additional parameters
 *
 * @return string Desired url in encoded form.
 */
function smarty_modifier_reviewsShowStars($score)
{
    $result = '';
    
    switch ($score)
    {
        case (10):
            for ($i = 1; $i < 10; $i++) {
                $result .= "<img src='/images/icons/extrasmall/favorites.png' />";
            }
            break;
        case (9):
                for ($i = 1; $i < 9; $i++) {
                $result .= "<img src='/images/icons/extrasmall/favorites.png' />";
            }
            break;
        case (8):
                for ($i = 1; $i < 8; $i++) {
                $result .= "<img src='/images/icons/extrasmall/favorites.png' />";
            }
            break;
        case (7):
                for ($i = 1; $i < 7; $i++) {
                $result .= "<img src='/images/icons/extrasmall/favorites.png' />";
            }
            break;
        case (6):
                for ($i = 1; $i < 6; $i++) {
