            }
            break;
        case (2):
                for ($i = 1; $i < 2; $i++) {
                $result .= "<img src='/images/icons/extrasmall/favorites.png' />";
            }
            break;
        case (1):
                for ($i = 1; $i < 1; $i++) {
                $result .= "<img src='/images/icons/extrasmall/favorites.png' />";
            }
            break;
            
            default:
                break;

    }

    return $result;
}
